import React, { useEffect, useState } from 'react';
import axios from 'axios';

function App() {
  const [message, setMessage] = useState('');

  useEffect(() => {
    axios.get('http://localhost:8000/api/disasters')
      .then(response => {
        console.log(response.data); // 結果をコンソールに表示
        setMessage('APIつながったよ！'); // 画面に表示
      })
      .catch(error => {
        console.error('APIエラー:', error);
        setMessage('APIエラーが発生しました');
      });
  }, []);

  return (
    <div style={{ padding: 20 }}>
      <h1>Laravel API との接続テスト</h1>
      <p>{message}</p>
    </div>
  );
}

export default App;
